                $accumulated_text = "" ;
                if ( $remaining_text =~ /^([a-z])(.*)$/ )
                {
                    $character_to_capitalize = $1 ;
                    $remaining_text = $2 ;
                    $accumulated_text .= uc( $character_to_capitalize ) ;
                }
                while ( $remaining_text =~ /^(.*?) ([a-z])(.*)$/ )
                {
                    $accumulated_text .= $1 ;
                    $character_to_capitalize = $2 ;
                    $remaining_text = $3 ;
