# function__get_cgi_information
#
sub function__get_cgi_information( ) {
my @name_value_pairs ;
my $raw_input ;
my $input_line ;
my $name_value ;
my $name ;
my $value ;
my $line_count ;
my $cgi_string_length_limit ;
my $cgi_line_length_limit ;
$cgi_string_length_limit = 100000 ;
$cgi_line_length_limit = 100 ;
$raw_input = '' ;
$line_count = 0 ;
$dashrep_get_replacement{ 'dashrep-list-of-input-cgi-phrase-names' } = '' ;
if ( ( exists( $ENV{ 'REQUEST_METHOD' } ) ) && ( $ENV{ 'REQUEST_METHOD' } eq 'GET' ) ) {
if ( ( exists( $ENV{ 'CONTENT_LENGTH' } ) ) && ( $ENV{ 'CONTENT_LENGTH' } > $cgi_string_length_limit ) ) {
$dashrep_get_replacement{ 'dashrep-error-cgi-input-too-long' } = 'yes' ;
$global_accumulator[ $global_accumulator_pointer ] .= ' dashrep_error_cgi_input_too_long' ;
return ;
}
if ( exists( $ENV{ 'QUERY_STRING' } ) ) {
$raw_input = $ENV{ 'QUERY_STRING' } ;
}
} elsif ( ( exists( $dashrep_get_replacement{ 'input-filename-for-cgi-information' } ) ) && ( $dashrep_get_replacement{ 'input-filename-for-cgi-information' } =~ /^[^ ]+\.[^ ]+$/ ) && ( open( INFILE , $dashrep_get_replacement{ 'input-filename-for-cgi-information' } ) ) ) {
while( $input_line = <INFILE> ) {
chomp( $input_line ) ;
if ( $line_count > 0 ) {
$raw_input .= ' ' ;
}
$raw_input .= $input_line ;
$line_count ++ ;
# end of while loop
}
close( INFILE ) ;
} elsif ( ( exists( $ENV{ 'REQUEST_METHOD' } ) ) && ( $ENV{ 'REQUEST_METHOD' } eq 'POST' ) ) {
while( $input_line = <STDIN> ) {
chomp( $input_line ) ;
if ( $line_count > 0 ) {
$raw_input .= ' ' ;
}
$raw_input .= $input_line ;
$line_count ++ ;
if ( $line_count > $cgi_line_length_limit ) {
$dashrep_get_replacement{ 'dashrep-error-cgi-input-too-long' } = 'yes' ;
$global_accumulator[ $global_accumulator_pointer ] .= ' dashrep_error_cgi_input_too_long' ;
return ;
}
# end of while loop
}
# end of if branch
}
$global_dashrep_replacement{ 'input-raw-cgi-string' } = $raw_input ;
if ( $raw_input =~ /^(([^ <>]+)=([^ <>]*)(&([^ <>]+)=([^ <>]*))*) *$/ ) {
$raw_input = $1 ;
$raw_input =~ tr/+/ / ;
@name_value_pairs = split( /&/, $raw_input ) ;
foreach $name_value ( @name_value_pairs ) {
( $name, $value ) = split( /=/, $name_value, 2 ) ;
if ( not( defined( $value ) ) ) {
$value = '' ;
}
$name =~ s/%([0-9A-F][0-9A-F])/chr (hex ($1))/ieg ;
$name =~ s/[<>\/\\]+/_/g ;
if ( $name eq 'texttoimport' ) {
# Special preservation of tabs, line breaks, etc. for imported text:
$value =~ s/%0D//ig ;
$value =~ s/%([0-9A-F][0-9A-F])/chr (hex ($1))/ieg ;
} else {
$value =~ s/%([0-9A-F][0-9A-F])/chr (hex ($1))/ieg ;
$value =~ s/[<>]+/_/gm ;
$value =~ s/\n/<eol\/>/gm ;
}
$name = &function_parameterized__remove_leading_trailing_spaces( $name ) ;
$value = &function_parameterized__remove_leading_trailing_spaces( $value ) ;
if ( $name ne '' ) {
# Prepend "input-" to the phrase name
$global_dashrep_replacement{ 'input-' . $name } = $value ;
if ( $dashrep_get_replacement{ 'dashrep-list-of-input-cgi-phrase-names' } ne '' ) {
$dashrep_get_replacement{ 'dashrep-list-of-input-cgi-phrase-names' } .= ' ' ;
}
$dashrep_get_replacement{ 'dashrep-list-of-input-cgi-phrase-names' } .= 'input-' . $name ;
}
# end of foreach loop
}
# end of if branch
}
$raw_input = '' ;
if ( exists( $ENV{ 'REMOTE_ADDR' } ) ) {
$global_dashrep_replacement{ 'input-ip-address' } = $ENV{ 'REMOTE_ADDR' } ;
}
if ( exists( $ENV{ 'REMOTE_PORT' } ) ) {
$global_dashrep_replacement{ 'input-port-number' } = $ENV{ 'REMOTE_PORT' } ;
}
if ( exists( $ENV{ 'HTTP_COOKIE' } ) ) {
$global_dashrep_replacement{ 'input-cookie-contents' } = $ENV{ 'HTTP_COOKIE' } ;
}
return '' ;
# end of function
}


