        if ( $possible_error_message eq "" )
        {
            $temp_text = $global_dashrep_replacement{ $operand_two } ;
            $temp_text =~ s/^ +// ;
            $temp_text =~ s/ +$//s ;
            if ( $temp_text =~ / / )
