            if ( ( exists( $global_dashrep_replacement{ "yes-or-no-use-two-spaces-as-column-delimiter" } ) ) && ( $global_dashrep_replacement{ "yes-or-no-use-two-spaces-as-column-delimiter" } eq "yes" ) )
