                }
            } elsif ( $action_name eq "numeric-logarithm-base-e" )
            {
                if ( $operand_one + 0.0 < 0.000001 )
                {
                    $result_value = -999999 ;
                } else
                {
                    $result_value = log( $operand_one + 0.0 ) ;
                }
                if ( $result_value == 0 )
                {
                    $action_result = "0" ;
                } else
                {
                    $action_result = sprintf( "%f" , $result_value ) ;
