        } elsif ( ( $action_name eq "find-line-in-file-that-begins-with-text" ) && ( ( not( exists( $global_dashrep_replacement{ $operand_two } ) ) ) || ( $global_dashrep_replacement{ $operand_two } !~ /[^ ]/ ) ) )
        {
